mapper.select:
SELECT * FROM Admin
ORDER BY adminKey DESC
LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}

SELECT adminKey, adminId, adminPw, adminStatus, createDate, role
FROM ADMIN
WHERE adminKey = #{adminKey}

mapper.update:
UPDATE Admin
SET adminId = #{adminId},
    adminPw = #{adminPw},
    adminStatus = #{adminStatus}
WHERE adminKey = #{adminKey}
